import React ,{Component} from 'react';
import './styles.css';

class Input extends Component{

    render(){
        return(

            <div className="input">

                { 
                    this.props.children!==""?  this.props.children.replace(/^-?\d*\.?/, '').toLocaleString(navigator.language , {
                        useGrouping: true,
                        maximumFractionDigits: 6
                    }):""






                //Math.round(this.props.children * 100)/100



                }
            </div>
        );


    }


}

export default Input;
